image_data = BytesIO(byte_data)
tanknadelImage = Image.open(image_data)

# Konvertieren des Bildes in ein unterst√ºtztes Format
if tanknadelImage.mode != 'RGB':
    pil_image = tanknadelImage.convert('RGB')
